from functools import cache
def a(n):
    @cache
    def h(n):
        if n <= 1: return 1
        return h(n - 1) * (2 * n - 1)
    @cache
    def c(n):
        if n == 0: return 1
        return h(n) - sum(h(k) * c(n - k) for k in range(1, n))
    return h(n) - c(n)
