from sympy import isprime, primerange
def sumdd(n): return sum(int(d)**2 for d in str(n))
def list(nn):
  for p in primerange(2, nn+1):
    s = sumdd(p)
    if isprime(p-s) and isprime(p+s): print(p, end=", ")
