from sympy import prime
def A181373(n):
    s, p, l = '', prime(n), 0
    for m in range(1,10**6):
        u = str(m)
        s += u
        l += len(u)
        t = s
        if not int(t) % p:
            for i in range(l-1):
                t = t[1:]+t[0]
                if int(t) % p:
                    break
            else:
                return m
    else:
        return 'search limit reached.' # _Chai Wah Wu_, Nov 12 2015

