# OEIS sequence: A181970

from sympy import primepi, integer_nthroot
def A181970(n):
    def bisection(f,kmin=0,kmax=1):
        while f(kmax) > kmax: kmax <<= 1
        kmin = kmax >> 1
        while kmax-kmin > 1:
            kmid = kmax+kmin>>1
            if f(kmid) <= kmid:
                kmax = kmid
            else:
                kmin = kmid
        return kmax
    def f(x): return n+x-sum(primepi(integer_nthroot(x,1<<i)[0]) for i in range(1,x.bit_length().bit_length()))
    m = bisection(f,n,n)
    return sum(primepi(integer_nthroot(m,1<<i)[0]) for i in range(m.bit_length().bit_length())) # _Chai Wah Wu_, Feb 18-19 2025

