from sympy import nextprime
def a(n):
    p,L,S=2,[],[]
    while len(L)<n-1:
        p,S1,i=nextprime(p),S,0
        while (i<len(L)) and ((len(S[i])+2<L[i]) or (p%L[i] in S[i])):
            S1[i].add(p%L[i])
            i=i+1
        if i==len(L):
            S1.append(set(L))
            S=S1
            L.append(p)
    return p
