from itertools import count
from sympy import integer_log
def A225858(n):
    def f(x):
        c = n
        for i in range(integer_log(x,3)[0]+1):
            i2 = 3**i
            for j in count(0):
                k = i2<<j
                if k>x:
                    break
                m = x//k
                c += (m-1)//12+(m-5)//12+2
        return c
    m, k = n, f(n)
    while m != k: m, k = k, f(k)
