# alternate version
def isconcat(n):
    if n < 10: return False
    s = str(n)
    mid = (len(s)+1)//2
    lft, rgt = int(s[:mid]), int(s[mid:])
    return lft - 1 == rgt
def afind(tlimit, tstart=0):
    for t in range(tstart, tlimit+1):
        trit = t*(t+1)//2
        if isconcat(trit):
            print(trit, end=", ")
