def DP(n):
  p = 1
  for i in str(n):
    p *= int(i)
  return p
def pal(n):
  r = ''
  for i in str(n):
    r = i + r
  return r == str(n)
{print(n, end=', ') for n in range(1, 10**6) if DP(n) and pal(n) and pal(n-DP(n))}
