from sympy import integer_log
def A233999(n):
    def bisection(f,kmin=0,kmax=1):
        while f(kmax) > kmax: kmax <<= 1
        kmin = kmax >> 1
        while kmax-kmin > 1:
            kmid = kmax+kmin>>1
            if f(kmid) <= kmid:
                kmax = kmid
            else:
                kmin = kmid
        return kmax
    def f(x):
        c = n+x
        for i in range(integer_log(x,49)[0]+1):
            m = x//49**i
            c -= (m-1)//7+(m-2)//7+(m-4)//7+3
        return c
    return bisection(f,n,n) # _Chai Wah Wu_, Feb 14 2025

