from sympy import isprime, nextprime
def agen(): # generator of terms
    p = 2
    while True:
        p3 = sum(3**i for i, bi in enumerate(bin(p)[2:][::-1]) if bi=='1')
        if isprime(p3):
            yield p3
        p = nextprime(p)
g = agen()
print([next(g) for n in range(1, 52)]) # _Michael S. Branicky_, Jan 16 2022

