import math
def getXY(n):
  y = math.isqrt(n*2)
  if n<=y*(y+1)//2: y-=1
  x = n - y*(y+1)//2
  return x, y
def a(n):
  ox, oy = getXY(n)
  nx, ny = getXY(2*n)
  return abs(nx-ox)+abs(ny-oy)
print([a(n) for n in range(1, 83)])

