from sympy import isprime
def afind(limit):
    f = 1 # (p-1)!
    s = 2 # sum(0! + 1! + ... + (p-1)!)
    for p in range(2, limit+1):
        if isprime(p):
            r_p = s%p
            if min(r_p, p-r_p) <= 2:
                print(p, end=", ")
        s += f*p
        f *= p
afind(11000) # _Michael S. Branicky_, Jan 03 2022

