# OEIS sequence: A236437

from sympy import prime, isprime
def A236174(n):
    p = prime(n)
    for b in range(2,11):
        x, y, z = p, 0, 1
        while x >= b:
            x, r = divmod(x,b)
            y += r*z
            z *= 10
        y += x*z
        if isprime(y):
            return y
A236437_list = [prime(n) for n in range(1,10**6) if A236174(n) == prime(n)]
