import sympy
from sympy import isprime
def PowOpp(x):
  if str(x).find('0') > -1:
    num = 0
    for i in str(x):
      num += x**int(i)
    if isprime(num):
      return True
x = 1
while x < 10**4:
  if PowOpp(x):
    print(x)
  x += 1

