# see link for a faster version
from itertools import count
def a(n, startfrom=2):
    if n == 1: return 1
    for k in count(max(2, startfrom)):
        P, passes = set(), True
        for i in range(k, k+n):
            for p in list(P):
                if i*p in P:
                    passes = False
                    break
                P.add(i*p)
            if not passes or i in P:
                break
            P.add(i)
        if passes:
            return k
print([a(n) for n in range(1, 21)]) # _Michael S. Branicky_, Feb 04 2025

