def a117966(n):
    if n==0: return 0
    if n%3==0: return 3*a117966(n//3)
    elif n%3==1: return 3*a117966((n - 1)//3) + 1
    else: return 3*a117966((n - 2)//3) - 1
def a(n):
    if n<2: return n
    x=a117966(n)
    if x<1: return 2*a(-x)
    else: return 1 + 2*a(x - 1)
