# OEIS sequence: A247190

from sympy import prime
def A247190(n):
    p, f, fv = prime(n), 1, {}
    for i in range(2,p):
        f = (f*i) % p
        if f in fv:
            return fv[f]
        else:
            fv[f] = i
    else:
        return 0 # _Chai Wah Wu_, Nov 25 2014

