from sympy import factorint, primepi
from sympy.ntheory.factor_ import core
def a048675(n):
    f=factorint(n)
    return 0 if n==1 else sum([f[i]*2**(primepi(i) - 1) for i in f])
def a(n): return a048675(core(n))
