# OEIS sequence: A254688

from sympy import isprime, nextprime
def A254688(n):
    y, x, n2 = 0, 2, 2*n
    while x < n:
        if isprime(n2-x) and isprime(n2-2*x+1):
            y += 1
        x = nextprime(x)
    return y # _Chai Wah Wu_, Feb 18 2015

