import sympy
def powdig(m):
    l=len(str(m))
    return(int(''.join([str(int(list(i)[1])**(l-list(i)[0])) for i in enumerate(list(str(m)))])))
n=2
while n>0:
    t=powdig(n)
    if sympy.isprime(t)==True:
        print(n)
    n=sympy.nextprime(n)

