from sympy import primepi, isprime
def A256420(n):
    r = int(primepi(n))
    def iterfun(f,n=0):
        m, k = n, f(n)
        while m != k: m, k = k, f(k)
        return m
    def f(x): return int(r+(m:=x>>1)-primepi(m))
    return iterfun(f,r) if isprime(n) else n<<1 # _Chai Wah Wu_, Oct 15 2024

