def nextpal(n,b): # returns the palindromic successor of n in base b
    m, pl = n+1, 0
    while m > 0:
        m, pl = m//b, pl+1
    if n+1 == b**pl:
        pl = pl+1
    n = (n//(b**(pl//2))+1)//(b**(pl%2))
    m = n
    while n > 0:
        m, n = m*b+n%b, n//b
    return m
n, a3, a5 = 0, 0, 0
while n <= 20000:
    if a3 < a5:
        a3 = nextpal(a3,3)
    elif a5 < a3:
        a5 = nextpal(a5,5)
    else: # a3 == a5
        print(n,a3)
        a3, a5, n = nextpal(a3,3), nextpal(a5,5), n+1
