# OEIS sequence: A265747

def greedyJ(n): m = (3*n+1).bit_length() - 1; return (m, (2**m-(-1)**m)//3)
def a(n):
    if n == 0: return 0
    place, value = greedyJ(n)
    return 10**(place-2) + a(n - value)
print([a(n) for n in range(49)]) # _Michael S. Branicky_, Jul 11 2021

