from sympy import isprime
def afind(limit, startk=1):
    pow2, pow3, pow5 = 2**startk, 3**startk, 5**startk
    for k in range(startk, limit+1):
        if isprime(pow2 + pow3 + pow5 - 3): print(k, end=", ")
        pow2 *= 2; pow3 *= 3; pow5 *= 5
