from sympy import divisors
def first(n):
    a, k = [-1], 4
    while len(a) <= n:
        if (len_d:=len(d:=divisors(k))) > 2: a.append(d[(len_d-1)//2] + d[-len_d//2])
        k += 1
    return a[1:n+1]
