from sympy import divisors
def first(n):
    a, k = [-1], 4
    while len(a) <= n:
        a.append(2 + k//2)
        if len(d:=divisors(k + 1)) > 2: a.append(d[1] + d[-2])
        k += 2
    return a[1:n+1]
print(first(100)) # _Karl-Heinz Hofmann_, Nov 16 2025

