# OEIS sequence: A277828

def a(m):
    if m == 1:
        return 1
    g = [2**i for i in range(1, m)]
    sg, lim, n = sum(g), 2**(m-1), m
    while True:
        g.append(sg)
        sg <<= 1
        sg -= g.pop(0)
        if g[-1] <= lim:
            return n
        lim <<= 1
        n += 1
print([a(i) for i in range(1, 15)])
