from sympy import factorint, primepi, primefactors
def a001222(n): return 0 if n==1 else a001222(n//primefactors(n)[0]) + 1
def a048675(n):
    if n==1: return 0
    f=factorint(n)
    return sum(f[i]*2**(primepi(i) - 1) for i in f)
def a(n): return a001222(a048675(n))
