from sympy import isprime, nextprime
def A283756(n):
    p=2
    m=2*n + 1
    while True:
        q=2*p**m - 1
        if (not isprime(q) or not isprime(2*q**m - 1)): p = nextprime(p)
        else: break
    return p # _Indranil Ghosh_, Mar 19 2017

