from sympy import factorint
def a(n):
    f=factorint(n)
    return 1 if n==1 else int("".join(bin(i)[2:] + bin(f[i])[2:] if f[i]!=1 else bin(i)[2:] for i in f))
print([a(n) for n in range(1, 101)]) # _Indranil Ghosh_, Jun 23 2017

