from itertools import islice
from gmpy2 import is_prime, next_prime
def agen(): # generator of terms
    alst = [3]
    while True:
        yield int(alst[-1])
        sums = [sum(alst[-i:]) for i in range(2, len(alst)+1, 2)]
        p = next_prime(alst[-1])
        while not all(is_prime(p+s) for s in sums):
            p = next_prime(p)
        alst.append(p)
print(list(islice(agen(), 18))) # _Michael S. Branicky_, Feb 01 2026

