from sympy.ntheory.factor_ import digits
def a(n):
    d = digits(n, 3)[1:]
    return int("".join('1' if i == 2 else '0' for i in d), 2)
print([a(n) for n in range(101)]) # _Indranil Ghosh_, Jul 20 2017

