def fmod(n, mod):
    return (pow(3, n, mod) + 2) % mod
def f(n):
    return pow(3, n) + 2
#terms is the number of terms to generate
terms = 20
for x in range(1, terms + 1):
    div = f(x)
    y = x + 1
    while fmod(y, div) != 0:
        y += 1
    print(y - x)

