# OEIS sequence: A298952

# 3-state DFAO (MSB-first, base 4)
def a(n):
    if n == 0: return 1
    s, digits = 0, []
    while n: digits.append(n % 4); n //= 4
    for d in reversed(digits):
        s = [[0,2,1,2],[1,2,2,1],[2,1,1,2]][s][d]
    return [0, 0, 1][s]
print([a(n) for n in range(121)])  # _Benoit Cloitre_, Feb 22 2026

