def A305380(n):
    m, tlist, s = 2**n, [1,2,4], 0
    while tlist[-1]+tlist[-2]+tlist[-3] <= m:
        tlist.append(tlist[-1]+tlist[-2]+tlist[-3])
    for d in tlist[::-1]:
        s *= 2
        if d <= m:
            s += 1
            m -= d
