# OEIS sequence: A305382

from sympy import prime, primefactors
def a(n):
    pn = prime(n)
    reach, expand = {pn}, [pn]
    while len(expand) > 0:
        p = expand.pop()
        for q in primefactors(2*p+1):
            if q not in reach:
                expand.append(q)
                reach.add(q)
    return len(reach)
print([a(n) for n in range(1, 101)]) # _Michael S. Branicky_, Jun 29 2022

