# OEIS sequence: A319281

def A319281(n):
    if n<3: return 15*n-14
    def bisection(f,kmin=0,kmax=1):
        while f(kmax) > kmax: kmax <<= 1
        kmin = kmax >> 1
        while kmax-kmin > 1:
            kmid = kmax+kmin>>1
            if f(kmid) <= kmid:
                kmax = kmid
            else:
                kmin = kmid
        return kmax
    def f(x): return n-1+x-sum((((x>>(i<<2))-1)>>4)+1 for i in range(x.bit_length()>>2))
    return bisection(f,n,n) # _Chai Wah Wu_, Feb 17 2025

