from functools import cache
@cache
def T(n, k):
    if k <= 0 or n <= 0: return 0
    if k == 1: return 1
    if k == n: return n * (n + 1) * 2**(n - 2)
    return T(n-1, k) + 2*T(n-1, k-1) - T(n-2, k-2)
for n in range(10): print([T(n, k) for k in range(n + 1)])
