from __future__ import division
def A321440(n): # a faster program based on the characterization in the comments
    if n == 0:
        return 1
    c = 0
    for i in range(n):
        mi = i*(i+1)//2 + n
        for j in range(i+1,n+1):
            k = mi - j*(j+1)//2
            if k < 0:
                break
            if not k % j:
                c += 1
    return c # _Chai Wah Wu_, Nov 09 2018

