import math
import matplotlib.pyplot as plt
num = 10000
x = []
y = []
# y is the main sequence
def sequence():
    a = 1
    y.append(a)
    for i in range(num):
        if (a != 1) and (math.gcd(a,i+1) == 1):
            a -= 1
        else:
            a += math.gcd(a,i+1)+1
        x.append(i)
        y.append(a)
    x.append(num)
sequence()
# code only regarding the plot.
plt.xlim(0,num)
plt.ylim(0,num)
plt.plot(x, y)
plt.xlabel('x - axis')
plt.ylabel('y - axis')
