from sympy import primerange
from sympy.ntheory.digits import digits
def isbalanced(converted):
    return sum([(place*2 - (len(converted) - 1))*digit for place, digit in enumerate(converted)]) == 0
balanced_primes_list = [prime for prime in primerange(20000) if(digits(prime, 3)[1:] != digits(prime, 3)[1:][::-1] and isbalanced(digits(prime, 3)[1:]))] # edited by _Jason Yuen_, Jan 18 2026

