from math import factorial
from itertools import accumulate
def A330527(n):
    c = a = factorial(n)
    blist = (0,1)
    for d in range(2,n+1):
        blist = tuple(accumulate(reversed(blist),initial=0))
        if n % d == 0:
            c += a*blist[-1]//factorial(d)
    return c # _Chai Wah Wu_, Apr 19 2023

