from gmpy2 import digits
def A337141(n):
    def f(x):
        l = (s:=digits(x,7)).find('6')
        if l >= 0: s = s[:l]+'5'*(len(s)-l)
        return n+int(s,6)
    m, k = n, f(n)
    while m != k: m, k = k, f(k)
