from gmpy2 import digits
def A337572(n):
    def f(x):
        l = (s:=digits(x,5)).find('4')
        if l >= 0: s = s[:l]+'3'*(len(s)-l)
        return n+int(s,4)
    m, k = n, f(n)
    while m != k: m, k = k, f(k)
