def aupto(lim):
  cubes = [i**3 for i in range(1, int(lim**(1/3))+2)]
  fours = [i**4 for i in range(1, int(lim**(1/4))+2)]
  return sorted(s+t for s in cubes for t in fours if t >= s and s+t <= lim)
