from sympy import isprime
def aupton(terms):
  alst = [2, 3]
  for n in range(3, terms+1):
    fnm2, fnm1 = alst[-2:]
    while not isprime(fnm2 + fnm1): fnm2, fnm1 = fnm1, fnm2+fnm1
    alst.append(fnm2 + fnm1)
  return alst
