from sympy import isprime, nextprime
def aupto(limit):
  p, q, num, alst = 3, 5, 7, []
  while num//3 <= limit:
    if num%3 == 0 and isprime(num//3): alst.append(num//3)
    p, q, num = q, nextprime(q), p**2 - p*q + q**2
  return alst
