from sympy import isprime
def ra(n): s = str(n); return int(s) + int(s[::-1])
def afind(limit):
    record = 0
    for k in range(limit+1):
        m, i = ra(k), 1
        while not isprime(m) and m%3 != 0 and m%11 != 0: m = ra(m); i += 1
        if isprime(m) and i > record: record = i; print(k, end=", ")
