from sympy import factorint
def aupto(limit):
    aset, prevsig = set(), [1]
    for k in range(3, limit+2):
        sig = sorted(factorint(k).values())
        if sig == prevsig == [1, 1]: aset.update([k - 1, k])
        prevsig = sig
    return sorted(aset)
