# OEIS sequence: A346676

def aupto(lim):
    s, pow3 = set(), 3
    while pow3 < lim:
        for j in range(1, (lim-pow3).bit_length()):
            s.add(2**j + pow3)
        pow3 *= 3
    return sorted(set(s))
